javascript_examples.auto_mode_javascript:
// ใช้ใน Node.js หรือ browser
async function translateVideo() {
    const videoFile = document.getElementById('videoFile').files[0];
    const reader = new FileReader();

    reader.onload = async function() {
        const base64Data = reader.result.split(',')[1];

        const response = await fetch('http://localhost:5678/webhook/video-translate', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                mode: 'auto',
                video_file: base64Data,
                source_language: 'th',
                target_language: 'en',
                voice_mode: 'female',
                video_speed: '1.0'
            })
        });

        const result = await response.json();
        console.log(result);
    };

    reader.readAsDataURL(videoFile);
}

javascript_examples.youtube_mode_javascript:
async function translateYouTubeVideo() {
    const response = await fetch('http://localhost:5678/webhook/video-translate', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            mode: 'youtube',
            youtube_url: 'https://www.youtube.com/watch?v=dQw4w9WgXcQ',
            source_language: 'en',
            target_language: 'th',
            voice_mode: 'male',
            video_speed: '2.0'
        })
    });

    const result = await response.json();
    console.log(result);
}

javascript_examples.check_status_javascript:
async function checkStatus(taskId) {
    const response = await fetch(`http://localhost:5556/api/status/${taskId}`);
    const data = await response.json();

    if (data.status === 'completed') {
        console.log('Processing completed!');
        const downloadUrl = `http://localhost:5556/api/download/${taskId}`;
        console.log(`Download URL: ${downloadUrl}`);
        return true;
    } else if (data.status === 'error') {
        console.log(`Error: ${data.message || 'Unknown error'}`);
        return true;
    } else {
        console.log(`Status: ${data.status}, Progress: ${data.progress || 0}%`);
        return false;
    }
}

// ตรวจสอบสถานะทุก 5 วินาที
async function monitorStatus(taskId) {
    while (!(await checkStatus(taskId))) {
        await new Promise(resolve => setTimeout(resolve, 5000));
    }
}
